mapper.select:
SELECT COUNT(*)
  FROM MEMBER_PARENT
 WHERE CELL_PHONE = #{parentCellPhone}
 
   AND CELL_PHONE != #{cellPhone}
 
 
   AND STORE_ID = #{storeId}

        SELECT COUNT(*)
          FROM MEMBER_PARENT
        
           
		   AND STORE_ID = #{storeId}

SELECT A.NAME, A.BIRTHDAY PARENT_BIRTHDAY, A.SEX PARENT_SEX, CASE WHEN LENGTH(A.CELL_PHONE) = 11 THEN CONCAT(SUBSTRING(A.CELL_PHONE,1,3),'-',SUBSTRING(A.CELL_PHONE,4,4),'-',SUBSTRING(A.CELL_PHONE,8,4)) ELSE CONCAT(SUBSTRING(A.CELL_PHONE,1,3),'-',SUBSTRING(A.CELL_PHONE,4,3),'-',SUBSTRING(A.CELL_PHONE,7,4)) END AS CELL_PHONE, A.EMAIL, REPLACE(A.ADDRESS,'|','') ADDRESS, SUBSTRING_INDEX(A.ADDRESS, '|', 1) ROAD_ADDRESS, CASE WHEN INSTR(A.ADDRESS,'|') > 0 THEN SUBSTRING_INDEX(A.ADDRESS, '|', -1) ELSE '' END AS DETAIL_ADDRESS, A.QR_CODE, DATE_FORMAT(A.REG_DATE, '%Y/%m/%d') REG_DATE, CASE WHEN A.STATUS = 'A' THEN 'Active' ELSE 'Inactive' END AS STATUS, (SELECT COUNT(*) FROM VOUCHER_SALE WHERE BUY_CELL_PHONE = A.CELL_PHONE) VOUCHER_COUNT, IFNULL((SELECT STORE_NAME FROM STORE S WHERE S.STORE_ID = (SELECT STORE_ID FROM VOUCHER_SALE WHERE BUY_CELL_PHONE = A.CELL_PHONE LIMIT 1)),'바우처없음') VOUCHER_STORE_NAME, B.STORE_NAME FROM MEMBER_PARENT A, STORE B
AND A.STORE_ID = B.STORE_ID
AND A.STORE_ID = #{storeId}

SELECT A.NAME, A.BIRTHDAY PARENT_BIRTHDAY, A.SEX PARENT_SEX, A.CELL_PHONE, A.EMAIL, REPLACE(A.ADDRESS,'|','') ADDRESS, SUBSTRING_INDEX(A.ADDRESS, '|', 1) ROAD_ADDRESS, CASE WHEN INSTR(A.ADDRESS,'|') > 0 THEN SUBSTRING_INDEX(A.ADDRESS, '|', -1) ELSE '' END AS DETAIL_ADDRESS, A.QR_CODE, B.NAME CHILD_NAME, B.BIRTHDAY, B.SEX, (SELECT GROUP_CONCAT(C.LEARNING_GRADE SEPARATOR ',') FROM MEMBER_CHILDREN_GRADE C WHERE C.PARENT_CELL_PHONE = A.CELL_PHONE AND C.CHILDREN_NAME = B.NAME) LEARNING_GRADE, A.STORE_ID FROM MEMBER_PARENT A, MEMBER_CHILDREN B WHERE A.CELL_PHONE = B.PARENT_CELL_PHONE
AND A.STORE_ID = #{storeId}
AND A.QR_CODE = #{qrCode}
AND A.NAME = #{name}
AND A.CELL_PHONE = #{cellPhone}

SELECT NAME, BIRTHDAY, SEX, CELL_PHONE, EMAIL, REPLACE(ADDRESS,'|','') ADDRESS, QR_CODE, STORE_ID FROM MEMBER_PARENT
AND QR_CODE = #{qrCode}
AND NAME = #{name}
AND CELL_PHONE = #{cellPhone}

SELECT A.NAME, A.BIRTHDAY, A.SEX, A.CELL_PHONE, A.EMAIL, REPLACE(A.ADDRESS,'|','') ADDRESS, A.QR_CODE, A.STORE_ID, B.NAME CHILD_NAME FROM MEMBER_PARENT A, MEMBER_CHILDREN B
AND A.CELL_PHONE = #{cellPhone}
AND A.CELL_PHONE = B.PARENT_CELL_PHONE

mapper.update:
UPDATE MEMBER_PARENT
NAME = #{parentName}, BIRTHDAY = #{parentBirthday}, SEX = #{parentSex}, CELL_PHONE = #{cellPhone}, EMAIL = #{email}, ADDRESS = #{address}, STORE_ID = #{storeId}, UPD_ID = #{updId}, UPD_DATE = NOW()
WHERE QR_CODE = #{qrCode}
