mapper.select:
SELECT A.PROMOTION_ID, A.PROMOTION_NAME, A.PROMOTION_CONTENTS, DATE_FORMAT(STR_TO_DATE(A.END_DATE,'%Y%m%d'), '%Y-%m-%d') END_DATE, A.PROMOTION_TYPE, A.PROMOTION_VALUE, A.PROMOTION_BANNER_ID, A.PROMOTION_DETAIL_IMAGE_ID, (SELECT GROUP_CONCAT(S.STORE_NAME SEPARATOR ',') FROM PROMOTION_STORE PS, STORE S WHERE A.PROMOTION_ID = PS.PROMOTION_ID AND PS.STORE_ID = S.STORE_ID) STORE_ID, A.REG_ID, DATE_FORMAT(A.REG_DATE, '%Y-%m-%d') REG_DATE, A.UPD_ID, DATE_FORMAT(A.UPD_DATE, '%Y-%m-%d') UPD_DATE, A.STATUS FROM PROMOTION A, PROMOTION_STORE B WHERE A.PROMOTION_ID = B.PROMOTION_ID
AND A.END_DATE >= DATE_FORMAT(NOW(), '%Y%m%d')
AND A.STATUS = 'ACTIVE' AND B.STORE_ID = #{storeId}

mapper.update:
UPDATE PROMOTION
PROMOTION_NAME = #{promotionName}, PROMOTION_CONTENTS = #{promotionContents}, END_DATE = #{endDate}, PROMOTION_TYPE = #{promotionType}, PROMOTION_VALUE = #{promotionValue}, PROMOTION_BANNER_ID = #{promotionBannerId}, PROMOTION_DETAIL_IMAGE_ID = #{promotionDetailImageId}, STATUS = #{status}, UPD_ID = #{updId}, UPD_DATE = NOW()
WHERE PROMOTION_ID = #{promotionId}
