mapper.select:
SELECT A.RESERVATION_ID, CASE WHEN LENGTH(A.CELL_PHONE) = 11 THEN CONCAT(SUBSTRING(A.CELL_PHONE,1,3),'-',SUBSTRING(A.CELL_PHONE,4,4),'-',SUBSTRING(A.CELL_PHONE,8,4)) ELSE CONCAT(SUBSTRING(A.CELL_PHONE,1,3),'-',SUBSTRING(A.CELL_PHONE,4,3),'-',SUBSTRING(A.CELL_PHONE,7,4)) END AS CELL_PHONE, IFNULL((SELECT NAME FROM MEMBER_PARENT WHERE CELL_PHONE = A.CELL_PHONE), '비회원') CUSTOMER_NAME, DATE_FORMAT(A.REG_DATE ,'%Y/%m/%d %H:%i') RESERVATION_SIGN_UP_DATE, DATE_FORMAT(B.SCHEDULE_START,'%Y/%m/%d') RESERVATION_DATE, DATE_FORMAT(B.SCHEDULE_START,'%H:%i') RESERVATION_TIME, (SELECT CASE WHEN GRADE = 'DISPOSABLE' THEN '1회권' ELSE '바우처' END FROM VOUCHER WHERE A.VOUCHER_NO = VOUCHER_NO) VOUCHER_TYPE, (SELECT CODE_VALUE FROM COMMON_CODE WHERE CODE_TYPE = 'RESERVATION_STATUS' AND CODE_KEY = A.STATUS) STATUS_NAME, A.VOUCHER_NO, A.SCHEDULE_ID, CONCAT(DATE_FORMAT(B.SCHEDULE_START,'%H:%i'),'~',DATE_FORMAT(B.SCHEDULE_END,'%H:%i')) SCHEDULE_TIME, B.NUMBER_OF_PEOPLE, B.TUTORING_NUMBER, A.STATUS, A.REG_ID, DATE_FORMAT(A.REG_DATE, '%Y/%m/%d') REG_DATE, A.UPD_ID, DATE_FORMAT(A.UPD_DATE, '%Y/%m/%d') UPD_DATE, A.CHILD_NAME, C.VOUCHER_USE_ID, CASE WHEN A.TUTORING_YN = 'Y' THEN '튜터링' ELSE (SELECT D.NAME FROM PLAY D WHERE D.PLAY_ID = B.PLAY_ID) END AS PLAY_NAME, DATE_FORMAT(D.REG_DATE, '%Y/%m/%d') MEMBER_REG_DATE, SUBSTRING_INDEX(D.ADDRESS, ' ', 2) ADDRESS, (SELECT DATE_FORMAT(M.BIRTHDAY, '%Y/%m/%d') FROM MEMBER_CHILDREN M WHERE M.PARENT_CELL_PHONE = A.CELL_PHONE AND M.NAME = A.CHILD_NAME) CHILD_BIRTHDAY, A.TUTORING_YN, (SELECT COUNT(*) FROM RESERVATION E WHERE B.SCHEDULE_ID = E.SCHEDULE_ID AND A.TUTORING_YN = 'N') RESERVATION_CNT, (SELECT COUNT(*) FROM RESERVATION E WHERE B.SCHEDULE_ID = E.SCHEDULE_ID AND A.TUTORING_YN = 'Y') TUTORING_CNT, (SELECT SUBSTRING_INDEX(S.STORE_NAME, ' ', - 1) FROM STORE S WHERE S.STORE_ID = B.STORE_ID) STORE_NAME_SHORT FROM RESERVATION A, SCHEDULE B, VOUCHER_USE C, MEMBER_PARENT D WHERE A.SCHEDULE_ID = B.SCHEDULE_ID AND A.RESERVATION_ID = C.RESERVATION_ID AND A.VOUCHER_NO = C.VOUCHER_NO AND A.CELL_PHONE = D.CELL_PHONE
AND B.STORE_ID = #{storeId}
AND C.VOUCHER_NO = 'FREE'
AND A.STATUS = #{status}
AND B.SCHEDULE_START BETWEEN DATE_FORMAT(STR_TO_DATE(CONCAT(#{scheduleStart}, '000000'), '%Y%m%d%H%i%S'),'%Y-%m-%d %H:%i:%S') AND DATE_FORMAT(STR_TO_DATE(CONCAT(#{scheduleStart}, '235959'), '%Y%m%d%H%i%S'),'%Y-%m-%d %H:%i:%S') GROUP BY A.RESERVATION_ID ORDER BY A.REG_DATE DESC

SELECT COUNT(*) 
  FROM RESERVATION
 WHERE CELL_PHONE = #{cellPhone}
   AND SCHEDULE_ID = #{scheduleId}
   AND STATUS = '02'

mapper.delete:
UPDATE RESERVATION
CELL_PHONE = #{cellPhone}, VOUCHER_NO = #{voucherNo}, SCHEDULE_ID = #{scheduleId}, STATUS = #{status}, UPD_ID = #{updId}, UPD_DATE = NOW()
WHERE RESERVATION_ID = #{reservationId}
