mapper.select:
SELECT COUNT(*)
  FROM NS_INSTRUCTOR
 WHERE TEL_NO = #{telNo}

mapper.update:
UPDATE STAFF_INFO
NAME = #{name}, NAME_ENG = #{nameEng}, TEL_NO = #{telNo}, ADDRESS = #{address}, START_DATE = #{startDate}, WORKER_TYPE = #{workerType}, STAFF_GRADE = #{staffGrade}, STORE_ID = #{storeId}, ID_PICTURE_IDX = #{idPictureIdx, jdbcType=VARCHAR}, HEALTH_CERTIFICATE_IDX = #{healthCertificateIdx, jdbcType=VARCHAR}, RESUME_IDX = #{resumeIdx, jdbcType=VARCHAR}, STATUS = #{status}, UPD_ID = #{updId}, UPD_DATE = NOW()
WHERE STAFF_ID = #{staffId}

UPDATE NS_INSTRUCTOR
NATIONALITY = #{nationality}, NAME = #{name}, FULL_NAME = #{fullName}, TEL_NO = #{telNo}, ADDRESS = #{address}, START_DATE = #{startDate}, STORE_ID = #{storeId}, PICTURE_IDX = #{pictureIdx, jdbcType=VARCHAR}, CRIMINAL_RECORDS_IDX = #{criminalRecordsIdx, jdbcType=VARCHAR}, RESUME_IDX = #{resumeIdx, jdbcType=VARCHAR}, UPD_ID = #{updId}, UPD_DATE = NOW()
WHERE NS_ID = #{nsId}
