mapper.select:
SELECT A.VOUCHER_NO, A.TICKET_NAME, A.GRADE, A.PRICE, A.USE_TIME, D.NAME, C.TIME_LEFT_HOUR, A.STORE_ID, (SELECT SUBSTRING_INDEX(S.STORE_NAME, ' ', - 1) FROM STORE S WHERE A.STORE_ID = S.STORE_ID) STORE_NAME_SHORT FROM VOUCHER A, VOUCHER_SALE B, VOUCHER_USE_VIEW C, MEMBER_CHILDREN D WHERE A.VOUCHER_NO = B.VOUCHER_NO AND B.VOUCHER_NO = C.VOUCHER_NO AND B.BUY_CELL_PHONE = #{buyCellPhone} AND A.STATUS = '12' AND C.TIME_LEFT_HOUR > 0 AND B.BUY_CELL_PHONE = D.PARENT_CELL_PHONE
AND A.STORE_ID = #{storeId}
GROUP BY A.VOUCHER_NO

SELECT DATE_FORMAT(A.REG_DATE, '%Y-%m-%d') SALE_DATE, (SELECT SUBSTRING_INDEX(S.STORE_NAME, ' ', - 1) FROM STORE S WHERE S.STORE_ID = A.STORE_ID) STORE_NAME, (SELECT SUBSTRING_INDEX(S.STORE_NAME, ' ', - 1) FROM STORE S WHERE S.STORE_ID IN (SELECT SI.STORE_ID FROM STAFF_INFO SI WHERE SI.STAFF_ID = A.REG_ID)) REG_STORE, A.TICKET_NAME, A.VOUCHER_NO, C.NAME, FORMAT(A.SALE_PRICE, 0) SALE_PRICE, CASE WHEN LENGTH(C.CELL_PHONE) = 11 THEN CONCAT(SUBSTRING(C.CELL_PHONE,1,3),'-',SUBSTRING(C.CELL_PHONE,4,4),'-',SUBSTRING(C.CELL_PHONE,8,4)) ELSE CONCAT(SUBSTRING(C.CELL_PHONE,1,3),'-',SUBSTRING(C.CELL_PHONE,4,3),'-',SUBSTRING(C.CELL_PHONE,7,4)) END AS CELL_PHONE, D.TIME_LEFT_HOUR, CASE WHEN IFNULL(P.PROMOTION_VALUE, 0) = 0 THEN '없음' WHEN P.PROMOTION_TYPE = 'ADD_TIME' THEN P.PROMOTION_VALUE ELSE '추가시간 프로모션아님' END AS PROMOTION, CASE WHEN IFNULL(P.PROMOTION_NAME, 0) = 0 THEN '없음' ELSE P.PROMOTION_NAME END AS PROMOTION_NAME, DATE_FORMAT(A.END_DATE, '%Y-%m-%d') END_DATE, CASE WHEN A.END_DATE < DATE_FORMAT(NOW(), '%Y%m%d') THEN '만료' WHEN A.STATUS = '04' THEN '정상' WHEN A.STATUS = '08' THEN '사용취소' ELSE (SELECT CC.CODE_VALUE FROM COMMON_CODE CC WHERE CC.CODE_TYPE = 'VOUCHER_STATUS' AND CC.CODE_KEY = A.STATUS) END AS STATUS, A.STATUS R_STATUS, CASE WHEN A.STATUS = '08' THEN DATE_FORMAT(A.UPD_DATE, '%Y-%m-%d') ELSE '정상' END AS CANCEL_DATE FROM VOUCHER A, VOUCHER_SALE B LEFT JOIN PROMOTION P ON B.PROMOTION_ID = P.PROMOTION_ID, MEMBER_PARENT C, VOUCHER_USE_VIEW D WHERE A.VOUCHER_NO = B.VOUCHER_NO
AND A.STORE_ID = #{storeId}
AND B.BUY_CELL_PHONE = C.CELL_PHONE AND B.VOUCHER_NO = D.VOUCHER_NO

mapper.update:
UPDATE VOUCHER_PRODUCT
GRADE = #{grade}, TICKET_NAME = #{ticketName}, PRICE = #{price}, SALE_PRICE = #{salePrice}, USE_TIME = #{useTime}, EXPIRE_MONTH = #{endDate}, IMAGE_IDX = #{imageIdx}, STATUS = #{status}, STORE_ID = #{storeId}, UPD_ID = #{updId}, UPD_DATE = NOW(),
WHERE PRODUCT_NO = #{productNo}

UPDATE VOUCHER
GRADE = #{grade}, TICKET_NAME = #{ticketName}, PRICE = #{price}, SALE_PRICE = #{salePrice}, USE_TIME = #{useTime}, END_DATE = #{endDate}, IMAGE_IDX = #{imageIdx}, STATUS = #{status}, STORE_ID = #{storeId}, MEMO = #{memo}, UPD_ID = #{updId}, UPD_DATE = NOW(),
WHERE VOUCHER_NO = #{voucherNo}

UPDATE VOUCHER_SALE
BUY_CELL_PHONE = #{buyCellPhone}, USED_CHILDREN_NAME = #{usedChildrenName}, PG_TYPE = #{pgType}, APPROVAL_NO = #{approvalNo}, STORE_ID = #{storeId}, BUY_CELL_PHONE = #{buyCellPhone}, PROMOTION_ID = #{promotionId}, UPD_ID = #{updId}, UPD_DATE = NOW(),
WHERE VOUCHER_NO = #{voucherNo}

UPDATE VOUCHER_USE
VOUCHER_NO = #{voucherNo}, USE_TIME = #{useTime}, STATUS = #{status}, RESERVATION_ID = #{reservationId}, UPD_ID = #{updId}, UPD_DATE = NOW(),
WHERE VOUCHER_USE_ID = #{voucherUseId}
